```lua
  it('should put cursor at the end of completed word', function()
    local line = 'vim._with({n|}, f)'
    local cursor_col = line:find('|') - 1
    line = line:gsub('|', '')
    local line_to_cursor = line:sub(1, cursor_col)
    local client_start_boundary = exec_lua(function ()
      return vim.fn.match(line_to_cursor, '\\k*$')
    end)
    local completed_word = 'noautocmd'

    local expected_cursor_column = client_start_boundary + #completed_word

    local cursor_column = exec_lua(function()
      local buf = vim.api.nvim_get_current_buf()
      vim.api.nvim_buf_set_lines(buf, 0, 1, true, {line})
      vim.api.nvim_win_set_cursor(0, {1, cursor_col})
      vim.cmd.startinsert()
      vim.api.nvim_buf_set_text(0, 0, client_start_boundary, 0, cursor_col, {completed_word})
      vim.api.nvim_set_vvar('completed_item', {
        abbr = 'noautocmd?',
        info = '',
        kind = 'Property',
        menu = '',
        user_data = {
          nvim = {
            lsp = {
              client_id = 1,
              completion_item = {
                data = {
                  id = 1,
                  uri = 'file:///tmp/test.lua'
                },
                insertText = 'noautocmd',
                insertTextFormat = 2,
                kind = 10,
                label = 'noautocmd?',
                sortText = '0001'
              }
            }
          }
        },
        word = 'noautocmd',
      })

      vim.api.nvim_exec_autocmds('CompleteDone', { buffer = buf, modeline = false })

      local _, col = unpack(vim.api.nvim_win_get_cursor(0))

      return col
    end)

    eq(expected_cursor_column, cursor_column)
  end)
```
