comment_regular_data.body:
made a patch that tries (poorly) to make parsing respect the timeout:

<details>

<summary>patch</summary>

```diff
diff --git a/lib/src/parser.c b/lib/src/parser.c
index 5fccc2ec..c76bb160 100644
--- a/lib/src/parser.c
+++ b/lib/src/parser.c
@@ -115,6 +115,7 @@ struct TSParser {
   TSRangeArray included_range_differences;
   unsigned included_range_difference_index;
   bool has_scanner_error;
+  bool finished_parsing;
 };
 
 typedef struct {
@@ -1990,6 +1991,7 @@ void ts_parser_reset(TSParser *self) {
   }
   self->accept_count = 0;
   self->has_scanner_error = false;
+  self->finished_parsing = false;
 }
 
 TSTree *ts_parser_parse(
@@ -2000,6 +2002,7 @@ TSTree *ts_parser_parse(
   TSTree *result = NULL;
   if (!self->language || !input.read) return NULL;
 
+  if (!self->finished_parsing) {
   if (ts_language_is_wasm(self->language)) {
     if (!self->wasm_store) return NULL;
     ts_wasm_store_start(self->wasm_store, &self->lexer.data, self->language);
@@ -2100,8 +2103,24 @@ TSTree *ts_parser_parse(
     }
   } while (version_count != 0);
 
+    array_clear(&self->tree_pool.tree_stack);
+
+    if (ts_subtree_child_count(self->finished_tree) > 0 && self->finished_tree.ptr->ref_count == 1) {
+      array_push(&self->tree_pool.tree_stack, ts_subtree_to_mut_unsafe(self->finished_tree));
+    }
+    self->finished_parsing = true;
+  }
+
   ts_assert(self->finished_tree.ptr);
-  ts_subtree_balance(self->finished_tree, &self->tree_pool, self->language);
+  while (true) {
+    if(ts_subtree_balance(&self->tree_pool, self->language)) break;
+    if (
+      ((self->cancellation_flag && atomic_load(self->cancellation_flag)) ||
+       (!clock_is_null(self->end_clock) && clock_is_gt(clock_now(), self->end_clock)))
+    ) {
+      return false;
+    }
+  }
   LOG("done");
   LOG_TREE(self->finished_tree);
 
diff --git a/lib/src/subtree.c b/lib/src/subtree.c
index 069f0467..1322d249 100644
--- a/lib/src/subtree.c
+++ b/lib/src/subtree.c
@@ -334,14 +334,12 @@ static void ts_subtree__compress(
   }
 }
 
-void ts_subtree_balance(Subtree self, SubtreePool *pool, const TSLanguage *language) {
-  array_clear(&pool->tree_stack);
-
-  if (ts_subtree_child_count(self) > 0 && self.ptr->ref_count == 1) {
-    array_push(&pool->tree_stack, ts_subtree_to_mut_unsafe(self));
-  }
-
+bool ts_subtree_balance(SubtreePool *pool, const TSLanguage *language) {
+  int ops_count = 0;
   while (pool->tree_stack.size > 0) {
+    if (ops_count >= 1000) return false;
+    ops_count++;
+
     MutableSubtree tree = array_pop(&pool->tree_stack);
 
     if (tree.ptr->repeat_depth > 0) {
@@ -351,6 +349,7 @@ void ts_subtree_balance(Subtree self, SubtreePool *pool, const TSLanguage *langu
       if (repeat_delta > 0) {
         unsigned n = (unsigned)repeat_delta;
         for (unsigned i = n / 2; i > 0; i /= 2) {
+          ops_count += i * 10;
           ts_subtree__compress(tree, i, language, &pool->tree_stack);
           n -= i;
         }
@@ -359,11 +358,13 @@ void ts_subtree_balance(Subtree self, SubtreePool *pool, const TSLanguage *langu
 
     for (uint32_t i = 0; i < tree.ptr->child_count; i++) {
       Subtree child = ts_subtree_children(tree)[i];
+      ops_count += 1;
       if (ts_subtree_child_count(child) > 0 && child.ptr->ref_count == 1) {
         array_push(&pool->tree_stack, ts_subtree_to_mut_unsafe(child));
       }
     }
   }
+  return true;
 }
 
