 Client: null-ls (id: 1, bufnr: [6])
 	filetypes:       lua, luau, java, python
 	autostart:       false
 	root directory:  /home/matei
 	cmd:             <function>
 
 Client: jdtls (id: 2, bufnr: [6])
 	filetypes:       java
 	autostart:       true
 	root directory:  Running in single file mode.
 	cmd:             /home/matei/.local/share/nvim/mason/bin/jdtls -configuration /home/matei/.cache/jdtls/config -data /home/matei/.cache/jdtls/workspace

